select * from ( SELECT ROWNUM RN, A.* FROM ( SELECT * FROM (
SELECT A.BAND_NO, A.BAND_NAME, A.BAND_INTRO, A.BAND_KEYWORD, A.BAND_OPEN_OPTION, A.BAND_LOC_OPTION, A.BAND_AGE_BEFOROPTION, A.BAND_AGE_AFTEROPTION, A.BAND_CATEGORY, A.BAND_GENDER_OPTION, A.BAND_MEMBERSHIP_OPTION, A.BAND_LOCATION, D.RECENTDAY, E.MEMCOUNT FROM BAND A, BAND_MEMBER_DETAIL B, BAND_BOARD_OPTION C, (SELECT MAX(BAND_BOARD_DATE) "RECENTDAY", BAND_BOARD_OPTION_NO FROM BAND_BOARD_DETAIL GROUP BY BAND_BOARD_OPTION_NO) D, (SELECT BAND_NO, COUNT(*) "MEMCOUNT" FROM BAND_MEMBER_DETAIL GROUP BY BAND_NO) E WHERE A.BAND_NO = B.BAND_NO
AND B.USER_ID = #{bandLeaderid} AND B.BAND_MEMBER_STATUS = 'BA02' AND B.BAND_KICK_STATUS = 'BB01' AND A.BAND_NO = C.BAND_NO(+) AND C.BAND_BOARD_OPTION_NO= D.BAND_BOARD_OPTION_NO(+) AND A.BAND_NO = E.BAND_NO(+) ORDER BY D.RECENTDAY DESC nulls last
)) A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

