SELECT BAND_NO "bandNo", BAND_LEADERID "bandLeaderId", BAND_NAME "bandName", BAND_INTRO "bandIntro", EXERSUB_NAME "bandCategory", BAND_OPEN_OPTION "bandOpenOption", BAND_AGE_OPTION "bandAgeOption", BAND_AGE_BEFOROPTION "bandAgeBeforoption", BAND_AGE_AFTEROPTION "bandAgeAfteroption", BAND_KEYWORD "bandKeyword", BAND_GENDER_OPTION "bandGenderOption", BAND_MEMBERSHIP_OPTION "bandMembershipOption", LOC_CITY "bandLocation", BAND_GENDER "bandGender" FROM ( SELECT ROWNUM RN, A.* FROM (
SELECT BAND_NO, BAND_LEADERID, BAND_NAME, BAND_INTRO, EXERSUB_NAME, BAND_OPEN_OPTION, BAND_AGE_OPTION, BAND_AGE_BEFOROPTION, BAND_AGE_AFTEROPTION, BAND_CATEGORY, BAND_KEYWORD, BAND_GENDER_OPTION, BAND_MEMBERSHIP_OPTION, LOC_CITY, BAND_GENDER FROM BAND A, EXER_SUB B, OWN_LOCATION C WHERE A.BAND_CATEGORY = B.EXERSUB_NO AND A.BAND_LOCATION=C.LOC_CODE AND BAND_OPEN_OPTION IN ('BC01', 'BC03')
) A
WHERE ROWNUM <= #{last}
) WHERE RN >= #{first}

select BAND_NO "bandNo", BAND_NAME "bandName", BAND_INTRO "bandIntro", BAND_KEYWORD "bandKeyword", BAND_OPEN_OPTION "bandOpenOption", BAND_AGE_OPTION "bandAgeOption", BAND_AGE_BEFOROPTION "bandAgeBeforoption", BAND_AGE_AFTEROPTION "bandAgeAfteroption", BAND_CATEGORY "bandCategory", BAND_GENDER_OPTION "bandGenderOption", BAND_MEMBERSHIP_OPTION "bandMembershipOption", BAND_LOCATION "bandLocation", RECENTDAY "recentday", MEMCOUNT "memcount" from ( SELECT ROWNUM RN, A.* FROM ( SELECT * FROM (
SELECT DISTINCT A.BAND_NO, A.BAND_NAME, A.BAND_INTRO, A.BAND_KEYWORD, A.BAND_OPEN_OPTION, A.BAND_AGE_BEFOROPTION, A.BAND_AGE_AFTEROPTION, A.BAND_CATEGORY, A.BAND_GENDER_OPTION, A.BAND_MEMBERSHIP_OPTION, A.BAND_LOCATION, A.BAND_AGE_OPTION, D.RECENTDAY, E.MEMCOUNT FROM BAND A, BAND_MEMBER_DETAIL B, BAND_BOARD_OPTION C, ( SELECT BAND_NO, MAX(RECENTDAY) "RECENTDAY" FROM ( SELECT A.BAND_NO, B.RECENTDAY FROM BAND_BOARD_OPTION A, (SELECT MAX(BAND_BOARD_DATE) "RECENTDAY", BAND_BOARD_OPTION_NO FROM BAND_BOARD_DETAIL GROUP BY BAND_BOARD_OPTION_NO) B WHERE A.BAND_BOARD_OPTION_NO=B.BAND_BOARD_OPTION_NO ) GROUP BY BAND_NO ) D, (SELECT BAND_NO, COUNT(*) "MEMCOUNT" FROM BAND_MEMBER_DETAIL where band_member_status = 'BA02' GROUP BY BAND_NO) E WHERE A.BAND_NO = B.BAND_NO
AND B.USER_ID = #{bandLeaderid} AND B.BAND_MEMBER_STATUS = 'BA02' AND B.BAND_KICK_STATUS = 'BB01' AND A.BAND_NO = C.BAND_NO(+) AND A.BAND_NO = D.BAND_NO(+) AND A.BAND_NO = E.BAND_NO(+)
AND BAND_NAME LIKE '%' || #{bandName} || '%'
ORDER BY D.RECENTDAY DESC nulls last
)) A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

SELECT COUNT(*) FROM ( SELECT A.BAND_NO, A.BAND_NAME, A.BAND_INTRO, A.BAND_KEYWORD, A.BAND_OPEN_OPTION, A.BAND_AGE_BEFOROPTION, A.BAND_AGE_AFTEROPTION, A.BAND_CATEGORY, A.BAND_GENDER_OPTION, A.BAND_MEMBERSHIP_OPTION, A.BAND_LOCATION FROM BAND A, BAND_MEMBER_DETAIL B WHERE A.BAND_NO = B.BAND_NO AND B.USER_ID = #{userId} AND B.BAND_MEMBER_STATUS = 'BA02' AND B.BAND_KICK_STATUS = 'BB01'
AND BAND_NAME LIKE '%' || #{bandName} || '%'
)

SELECT BAND_NO "bandNo", BAND_BOARD_CONTENT "bandBoardContent", A.BAND_BOARD_DETAIL_NO "bandBoardDetailNo", BAND_BOARD_DATE "bandBoardDate", BAND_BOARD_TITLE "bandBoardTitle", BAND_BOARD_WRITER "bandBoardWriter", RANK "rank", REPLY_COUNT "replyCount", Band_NAME "bandName" FROM( SELECT BAND_NO, Band_NAME, BAND_BOARD_CONTENT, BAND_BOARD_DETAIL_NO, BAND_BOARD_DATE, BAND_BOARD_TITLE, BAND_BOARD_WRITER, RANK FROM( SELECT BAND_NO, Band_NAME, BAND_BOARD_CONTENT, BAND_BOARD_DETAIL_NO, BAND_BOARD_DATE, BAND_BOARD_TITLE, BAND_BOARD_WRITER, ROW_NUMBER() OVER(PARTITION BY Z.BAND_NO ORDER BY Z.BAND_NO, Z.BAND_BOARD_DATE DESC) RANK FROM (SELECT A.BAND_NO, A.Band_NAME, D.BAND_BOARD_CONTENT, D.BAND_BOARD_DETAIL_NO, D.BAND_BOARD_DATE, D.BAND_BOARD_TITLE, D.BAND_BOARD_WRITER FROM BAND A, BAND_BOARD_OPTION C, BAND_BOARD_DETAIL D WHERE A.BAND_NO IN
#{b}
AND A.BAND_NO = C.BAND_NO AND C.BAND_BOARD_OPTION_NO = D.BAND_BOARD_OPTION_NO) "Z"
) WHERE RANK <=3) A,
(SELECT BAND_BOARD_DETAIL_NO, COUNT(*) REPLY_COUNT FROM BAND_BOARD_DETAIL A, REPLY B WHERE A.BAND_BOARD_DETAIL_NO=B.CATEGORY_NO GROUP BY BAND_BOARD_DETAIL_NO) B WHERE A.BAND_BOARD_DETAIL_NO=B.BAND_BOARD_DETAIL_NO(+) ORDER BY BAND_BOARD_DATE DESC
