mapper.select:
SELECT COUNT(*) FROM BAND_MEMBER_DETAIL
WHERE BAND_NO = #{bandNo}

       AND BAND_MEMBER_STATUS = #{bandMemberStatus}

SELECT B.*,M.MEDIA_SERVER_FILE AS MEDIASERVERFILE FROM BAND B, MULTIMEDIA M
WHERE B.BAND_NO = M.IDENTIFY_ID
AND B.BAND_NO = #{bandNo}

mapper.update:
UPDATE BAND SET BAND_STATUS = 'BS_2',
                BAND_DISABLETIME = SYSDATE + 30
                WHERE BAND_NO = #{bandNo}

UPDATE BAND SET BAND_STATUS = 'BS_1',
                BAND_DISABLETIME = null
                WHERE BAND_NO = #{bandNo}

mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, AN.* from (
SELECT M.USER_ID , M.BAND_NICKNAME, M.BAND_NO, M.BAND_ACCESS_DATE BAND_ACCESS_DATE, M.BAND_BIRTH BAND_BIRTH, M.BAND_GENDER BAND_GENDER, M.BAND_MEMBER_INTRO BAND_MEMBER_INTRO, M.BAND_SIGNUP_DATE BAND_SIGNUP_DATE, M.BAND_MEMBER_NO BAND_MEMBER_NO , ( TRUNC(MONTHS_BETWEEN(TRUNC(SYSDATE), M.BAND_BIRTH) / 12 ) + 2 ) AGE , ( SELECT COUNT(*) FROM BAND_BOARD_DETAIL A, BAND B, BAND_BOARD_OPTION C WHERE B.BAND_NO=C.BAND_NO AND C.BAND_BOARD_OPTION_NO=A.BAND_BOARD_OPTION_NO AND A.BAND_BOARD_WRITER = M.USER_ID AND C.BAND_NO = #{bandNo} )BOARD_CNT , ( ( SELECT COUNT(*) FROM BAND A, BAND_BOARD_OPTION B, BAND_BOARD_DETAIL C, REPLY D WHERE A.BAND_NO = B.BAND_NO AND B.BAND_BOARD_OPTION_NO = C.BAND_BOARD_OPTION_NO AND C.BAND_BOARD_DETAIL_NO = D.CATEGORY_NO AND D.USER_ID = M.USER_ID AND B.BAND_NO = #{bandNo}) + ( SELECT COUNT(*) FROM BAND A, BAND_BOARD_OPTION B, BAND_BOARD_DETAIL C, REPLY D, RE_REPLY E WHERE A.BAND_NO = B.BAND_NO AND B.BAND_BOARD_OPTION_NO = C.BAND_BOARD_OPTION_NO AND C.BAND_BOARD_DETAIL_NO = D.CATEGORY_NO AND D.REPLY_NO = E.REPLY_NO AND E.USER_ID = M.USER_ID AND B.BAND_NO = #{bandNo}) )REPLY_CNT , ( SELECT A.MEDIA_SERVER_FILE FROM MULTIMEDIA A WHERE A.IDENTIFY_ID = M.BAND_MEMBER_NO ) PROFILE_IMG FROM BAND_MEMBER_DETAIL M WHERE BAND_MEMBER_STATUS = 'BA02'
AND (USER_ID LIKE '%' || #{bandNickname} || '%' OR BAND_NICKNAME LIKE '%' || #{bandNickname} || '%')
AND BAND_NO = #{bandNo} ORDER BY
BAND_NICKNAME BAND_ACCESS_DATE BOARD_CNT DESC REPLY_CNT DESC BAND_SIGNUP_DATE BAND_SIGNUP_DATE
) AN WHERE ROWNUM <= #{last}) where RN>= #{first}

	SELECT * FROM ( SELECT ROWNUM RN, AN.* FROM ( SELECT  USER_ID, BAND_NICKNAME, BAND_MEMBER_INTRO, BAND_GENDER
			, ( TRUNC(MONTHS_BETWEEN(TRUNC(SYSDATE), BAND_BIRTH) / 12 ) + 2 
            ) AGE
            , ( SELECT A.MEDIA_SERVER_FILE
	        	FROM MULTIMEDIA A
	        	WHERE A.IDENTIFY_ID = BAND_MEMBER_NO
	        ) PROFILE_IMG
	FROM BAND_MEMBER_DETAIL 
	WHERE BAND_MEMBER_STATUS = 'BA01'

	    AND USER_ID LIKE '%' || #{userId} || '%'
		OR BAND_NICKNAME LIKE '%' || #{userId} || '%'

	AND BAND_NO = #{bandNo}
	ORDER BY BAND_SIGNUP_DATE 
	) AN WHERE ROWNUM  <= #{last}) where RN>= #{first}

SELECT * FROM ( SELECT ROWNUM RN, AN.* FROM ( SELECT USER_ID, BAND_NICKNAME, BAND_MEMBER_KICK_REASON, BAND_KICK_DATE, BAND_MEMBER_REJOIN, ( SELECT A.MEDIA_SERVER_FILE FROM MULTIMEDIA A WHERE A.IDENTIFY_ID = BAND_MEMBER_NO ) PROFILE_IMG
FROM BAND_MEMBER_DETAIL WHERE BAND_MEMBER_STATUS = 'BA03'
AND USER_ID LIKE '%' || #{userId} || '%' OR BAND_NICKNAME LIKE '%' || #{userId} || '%'
AND BAND_NO = #{bandNo} ORDER BY BAND_KICK_DATE
) AN WHERE ROWNUM <= #{last}) WHERE RN>= #{first}

mapper.update:
UPDATE BAND_MEMBER_DETAIL 
   SET BAND_MEMBER_STATUS = 'BA01' 
 WHERE BAND_NO = #{bandNo}
   AND USER_ID = #{userId}
