mapper.update:
		UPDATE CHALL_RESULT
        SET MEM_VLD = (SELECT COUNT(*)
                        FROM CHALL_VALIDATION
                        WHERE USER_ID = #{userId}
                        AND CHALL_NO = #{challNo} ),
            SUCCESS_RATE = (SELECT ROUND (MEM_VLD/CHALL_VLD, 2) 
                            FROM CHALL_RESULT
                            WHERE CHALL_NO = #{challNo}
                            AND USER_ID = #{userId})
    	WHERE CHALL_NO = #{challNo}
        AND USER_ID = #{userId}
