mapper.select:
SELECT * FROM CHALL_RESULT
 WHERE CHALL_NO = #{challNo}
   AND USER_ID = #{userId}

		SELECT ROUND(AVG(SUCCESS_RATE),2)
          FROM CHALL_RESULT
         WHERE USER_ID = #{userId}

SELECT AVG(SUCCESS_RATE) * 100 AS AVGSR ,  NVL( SUM(REWARD_PRICE) , 0) AS SUMRP
FROM CHALL_RESULT
WHERE USER_ID = #{userId}
