mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM (
SELECT C.*, M.MEDIA_SERVER_FILE AS FIRST_IMAGE FROM MULTIMEDIA M, CHALLENGES C WHERE M.IDENTIFY_ID = C.CHALL_NO
AND MEDIA_NO IN ( SELECT MIN (M.MEDIA_NO) FROM CHALLENGES S, MULTIMEDIA M WHERE S.CHALL_NO=M.IDENTIFY_ID GROUP BY S.CHALL_NO ) ORDER BY TO_NUMBER(SUBSTR(CHALL_NO,5))
) A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}

SELECT * FROM ( SELECT ROWNUM RN, A.* FROM (
SELECT C.*, M.MEDIA_SERVER_FILE AS FIRST_IMAGE FROM MULTIMEDIA M, (SELECT * FROM CHALLENGES WHERE CHALL_NO IN ( SELECT CHALL_NO FROM CHALL_MEM_LIST WHERE USER_ID = #{userId} AND MEM_STATUS = '승인')) C WHERE M.IDENTIFY_ID = C.CHALL_NO AND MEDIA_NO IN ( SELECT MIN (M.MEDIA_NO) FROM CHALLENGES S, MULTIMEDIA M WHERE S.CHALL_NO=M.IDENTIFY_ID GROUP BY S.CHALL_NO )
) A WHERE ROWNUM <= #{last} ) WHERE RN >= #{first}
